import logo from './logo.svg';
import './App.css';

import { useState } from 'react';
import Renderer from './components/Renderer';
import Sidebar from './components/Sidebar'
import ParamList from "./components/ParamList";

function App() {
  let links = [];
  let images = [];
  let textElements = [];
  const [params, SetParams] = useState({});
  const [htmlContent, SetHTML] = useState("");
  const paramregex = /{{[^({})]+}}/g
  let currentHighlight = null

  const LoadHTML = (event) => {
      var file = event.target.files[0]
      if (file) {
          var reader = new FileReader();
          reader.readAsText(file, "UTF-8");
          reader.onload = function (evt) {
            SetHTML(evt.target.result)  
            // document.getElementById("renderer").dangerouslySetInnerHTML = evt.target.result;
            //   // Get all elements in the document
            SetUpEditor()
          }
          reader.onerror = function (evt) {
              document.getElementById("renderer").innerHTML = "error reading file";
              console.log(evt)
          }
      }
  }

  const SetUpEditor = ()=>{
      const allElements = document.getElementById("renderer").querySelectorAll('*');

      textElements.length = 0;
      images.length = 0;
      links.length = 0;
      console.log(allElement)

      allElements.forEach(element => {
          // Check if the element has only text nodes (no nested HTML tags)
          const hasText = Array.from(element.childNodes).some(
              node => node.nodeType === Node.TEXT_NODE
          );

          if (hasText && element.textContent.trim() !== '') {
          textElements.push(element);
          console.log(element)
          }
      });

      links = (document.getElementById("renderer").querySelectorAll("a"))
      links.forEach(lmnt => {
          if(lmnt.textContent.trim() === ''){
              lmnt.onClick = PrintSomething;
          }
          console.log(lmnt.href)
          lmnt.onDoubleClick = (e)=>{
              e.preventDefault();
              const newlink = window.prompt("Set This Hyperlink", e.target.closest("a").getAttribute("href"))
              if (newlink !== null){
                  e.target.closest("a").href = newlink
                  DetectParams(e.target.closest("a"), newlink)
              }
          }
      })

      images = (document.getElementById("renderer").querySelectorAll("img"));
      images.forEach(lmnt => {
          lmnt.onClick = e =>{
              if(e.ctrlKey){
                  e.preventDefault();
                  const newsrc = window.prompt("Set This Image Source", e.target.closest("img").getAttribute("src"));
                  if (newsrc !== null && newsrc !== 'none'){
                      e.target.closest("img").src = newsrc;
                      DetectParams(e.target.closest("img"), newsrc)
                  }
              }
          }
      })
  }
  function DetectParams (element, value){
      // console.log(params)
      Object.keys(params).forEach(k =>{
          const lst = params[k]
          const index = lst.indexOf(element)
          if(index > -1) lst.splice(index)
          if(lst.length === 0){
              delete params[k]
          }
      })

      const foundparams = [...value.matchAll(paramregex)]
      foundparams.forEach(prm =>{
          prm = ''+prm+''
          prm = prm.substring(2, prm.length - 2)
          if (params[prm] == null){
              params[prm] = []
          }
          params[prm].push(element)
      })
      return params
  }

  const PrintSomething = (e)=>{
      e.preventDefault()
      console.log("yo")
  }

  return (
    <div className='App'>
      <div className="sidenav" id='sidebar'>
          <label>HTML Upload<input type="file" onChange={LoadHTML}></input></label>
          <label>CSV Upload<input type="file"></input></label>
          {/* <button onClick={ReloadParams()}>Refresh Params List</button> */}
          <ParamList params={params}></ParamList>
      </div>
      <div id="renderer" dangerouslySetInnerHTML={{__html:htmlContent}}></div>
    </div>
  );
}

export default App;
